import React from "react";
import Sketch from "react-p5";

let array = [];
let visited = [];
let n;
let side = 50;
let key;
let index = 0;
let found = false;

class LinearSearch extends React.Component {
  componentDidMount() {
    this.resetArray();
    console.log(array);
  }

  resetArray() {
    for (let i = 0; i < n; i++) {
      array.push(Math.floor(Math.random() * 50 + 1));
    }
    for (let i = 0; i < n; i++) {
      visited.push(false);
    }
    key = array[Math.floor(Math.random() * 19)];
  }

  setup = (p5, parent) => {
    let xyz = p5.createCanvas(1000, 80);
    let x = (p5.windowWidth - p5.width) / 2;
    let y = (p5.windowHeight - p5.height) / 2;
    xyz.position(x, y);
    p5.frameRate(1);
    n = p5.width / side;
  };

  draw = (p5) => {
    if (!found) {
      p5.background(255);
      for (let i = 0; i < n; i++) {
        if (!visited[i]) {
          p5.fill(4, 228, 221);
        } else {
          p5.fill(0, 0, 255);
        }
        p5.stroke(0);
        p5.rect(i * side, 0, side, side);
        p5.fill(0);
        p5.textSize(15);
        p5.text(array[i], i * side + (9 * side) / 24, (7 * side) / 12);
      }

      if (index != -1) {
        if (array[index] == key) {
          p5.fill(255, 0, 0);
          found = true;
        } else {
          visited[index] = true;
          p5.fill(0, 255, 0);
        }
      }
      p5.rect(index * side, 0, side, side);
      p5.fill(0);
      p5.textSize(15);
      p5.text(array[index], index * side + (9 * side) / 24, (7 * side) / 12);
      index = index + 1;
    }
  };

  render() {
    return <Sketch setup={this.setup} draw={this.draw} />;
  }
}

export default LinearSearch;
