import React from 'react'
import Sketch from 'react-p5';

let array = [];
let n;
let side = 50;
let key;

class LinearSearch extends React.Component{
    componentDidMount(){
        this.resetArray();
        console.log(array);
    }

    resetArray(){
        for(let i = 0 ; i < n ; i++){
            array.push(Math.floor(Math.random() * 100 + 1));
        }
        key = array[Math.floor(Math.random() * 19)];
    }

    setup = (p5, parent) => {
        console.log(1);
        let xyz = p5.createCanvas(1000, 80);
        let x = (p5.windowWidth - p5.width) / 2;
        let y = (p5.windowHeight - p5.height) / 2;
        xyz.position(x, y);
        n = p5.width/side;
    }

    draw = (p5) => {
        p5.background(255);
        for(let i = 0 ; i < n ; i++){
            p5.fill(4, 228, 221);
            p5.stroke(0);
            p5.rect(i*side, 0, side, side);
            p5.fill(0);
            p5.textSize(15);
            p5.text(array[i], i*side + (9*side)/24, (7*side)/12);
        }

        for(let i = 0 ; i < n ; i++){
            if(array[i] == key){
                p5.fill(255, 0, 0);
                p5.rect(i*side, 0, side, side);
                p5.noLoop();
            }
        }
    } 

    render(){
        return (
            <Sketch setup={this.setup} draw={this.draw}/>
        );
    }
}

export default LinearSearch;
