import React from "react";
import Sketch from "react-p5";
import Point from "./Point";
import NavBar from "./NavBar";

let points = [];
const width = 800;
const height = 500;
let minDistance = 9999999;
let minDistancePath = [];
class TSP extends React.Component {
  state = { stage: 0 };

  setup = (p5, parent) => {
    let xyz = p5.createCanvas(width, height).parent(parent);
    let x = (p5.windowWidth - p5.width) / 2;
    let y = (p5.windowHeight - p5.height) / 2;
    xyz.position(x, y);
  };

  draw = (p5) => {
    p5.background(0);
    if (this.state.stage === 0) {
      p5.noStroke();
      p5.fill(255);
      p5.noCursor();
      p5.ellipse(p5.mouseX, p5.mouseY, 12);
      for(let i = 0 ; i < points.length ; i++){
        order[i] = i;
      }
    }
    this.showPoints(p5);
    if(this.state.stage === 1){
        let i = Math.floor(Math.random(1) * points.length);
        let j = Math.floor(Math.random(1) * points.length);
        this.swap(i , j);
        this.showLines(points, p5, 255, 255, 255, 2);
        let currentDistance = this.calcPathDistance(p5);
        if(currentDistance < minDistance){
            minDistance = currentDistance;
            minDistancePath = points.slice();
            console.log(minDistance);
        }
        this.showLines(minDistancePath, p5, 255, 0, 255, 4);
    }
  };

  calcPathDistance = (p5) => {
    let dist = 0;
    for(let i = 1 ; i < points.length ; i++){
        dist += p5.dist(points[i].x, points[i].y, points[i-1].x, points[i-1].y);
    }
    return dist;
  }

  swap(i, j){
    let temp = points[i];
    points[i] = points[j];
    points[j] = temp;
  }

  showLines = (points, p5, r, g, b, strokeWeight) => {
    p5.beginShape();
    p5.stroke(r, g, b);
    p5.strokeWeight(strokeWeight);
    p5.noFill();
    for(let i = 0 ; i < points.length ; i++){
        p5.vertex(points[i].x, points[i].y);
    }
    p5.endShape();
  }

  showPoints = (p5) => {
    for (let i = 0; i < points.length; i++) {
        p5.fill(points[i].r, points[i].g, points[i].b);
        p5.ellipse(points[i].x, points[i].y, 15, 15);
      }
  }

  mousePressed = (e) => {
    if (this.state.stage === 0) {
      let x = e.mouseX;
      let y = e.mouseY;
      if(x > 0 && x < width && y > 0 && y < height){
        points.push(new Point(x, y));
      }
    }
  };

  onBackButtonClick = () => {
      points = [];
    this.props.onBackButtonClick();
  };

  onClearButtonClick = () => {
      points = [];
      this.setState({stage : 0});
  };

  onStartButtonClick = () => {
    this.setState({ stage: 1 });
  };

  render() {
    return (
      <div>
        <NavBar
          stage={this.state.stage}
          onBackButtonClick={this.onBackButtonClick}
          onClearButtonClick={this.onClearButtonClick}
          onStartButtonClick={this.onStartButtonClick}
        />
        <Sketch
          setup={this.setup}
          draw={this.draw}
          mousePressed={this.mousePressed}
        />
      </div>
    );
  }
}

export default TSP;
