import React from 'react';
import Sketch from 'react-p5';

let points = [];

class TSP extends React.Component{    
    state = {points : []};

    setup = (p5, parent) => {
        let xyz = p5.createCanvas(500, 400).parent(parent);
        let x = (p5.windowWidth - p5.width) / 2;
        let y = (p5.windowHeight - p5.height) / 2;
        xyz.position(x, y);
    };

    draw = (p5) => {
        p5.background(0);
        for(let i = 0 ; i < points.length ; i++){
            p5.fill(p5.random(255));
            p5.ellipse(points[i][0], points[i][1], 15, 15);
        }
    };
     
    mousePressed = (e) => {
        let x = e.mouseX;
        let y = e.mouseY;
        points.push([x, y]);
        this.setState({points});
    };
    
    render(){
        return(
            <Sketch setup={this.setup} draw={this.draw} mousePressed={this.mousePressed}/>
         );
    }

}

export default TSP;
