import React from "react";
import Sketch from "react-p5";

let array = [];
let n;
let side = 50;
let key;
let found = false;
let lo, hi, mid;

class BinarySearch extends React.Component {
  componentDidMount() {
    this.resetArray();
    lo = 0;
    hi = n - 1;
  }

  resetArray() {
    for (let i = 0; i < n; i++) {
      array.push(Math.floor(Math.random() * 100 + 1));
      array.sort((a, b) => (a-b));
    }
    key = array[Math.floor(Math.random() * 19)];
  }

  setup = (p5, parent) => {
    let xyz = p5.createCanvas(1000, 80);
    let x = (p5.windowWidth - p5.width) / 2;
    let y = (p5.windowHeight - p5.height) / 2;
    xyz.position(x, y);
    p5.frameRate(0.5);
    n = p5.width / side;
  };

  draw = (p5) => {
      if (!found && lo <= hi) {
      p5.background(255);
      for (let i = 0; i < n; i++) {
        if (i >= lo && i <= hi) {
          p5.fill(0, 255, 0);
        } else {
          p5.fill(255);
        }
        p5.stroke(0);
        p5.rect(i * side, 0, side, side);
        p5.fill(0);
        p5.textSize(15);
        p5.text(array[i], i * side + (9 * side) / 24, (7 * side) / 12);
      }
      mid = Math.floor((lo + hi) / 2);
      if (array[mid] === key) {
        found = true;
        p5.fill(255, 0, 0);
        p5.rect(mid * side, 0, side, side);
        p5.fill(0);
        p5.textSize(15);
        p5.text(array[mid], mid * side + (9 * side) / 24, (7 * side) / 12);
      } else if (array[mid] > key) {
        hi = mid - 1;
      } else if (array[mid] < key) {
        lo = mid + 1;
      }
    }
  };

  render() {
    return <Sketch setup={this.setup} draw={this.draw} />;
  }
}

export default BinarySearch;
